mapper.select:
SELECT * FROM Admin WHERE 1=1 

	AND realName like '%${realName}%'


	AND createTime >= #{startTime}


	AND createTime <= #{endTime}


	AND phone = #{phone}


	AND weixin = #{weixin}


	AND FIND_IN_SET(id, getChildLst(#{parentId}))

limit #{start},#{length}

SELECT count(*) FROM Admin WHERE 1=1 

	AND realName like '%${realName}%'


	AND createTime >= #{startTime}


	AND createTime <= #{endTime}


	AND phone = #{phone}


	AND weixin = #{weixin}


	AND FIND_IN_SET(id, getChildLst(#{parentId}))

mapper.insert:

	SELECT LAST_INSERT_ID()

INSERT INTO Admin(
