mapper.update:
UPDATE Admin
SET 
id = #{id}

	,name = #{name}


	,password = #{password}


	,createTime = #{createTime}


	,updateTime = #{updateTime}


	,description = #{description}


	,realName = #{realName}


	,phone = #{phone}


	,status = #{status}



	,identityCode = #{identityCode}


	,weixin = #{weixin}


	,qq = #{qq}


	,homeAddress = #{homeAddress}


	,remark = #{remark}


WHERE 
id=#{id}
