mapper.select:
SELECT * FROM Admin WHERE 1=1 

	AND realName like '%${realName}%'

limit #{start},#{length}

mapper.insert:

	SELECT LAST_INSERT_ID()

INSERT INTO Admin(
id

	,name


	,password


	,createTime


	,updateTime


	,description


	,realName


	,phone


	,status

)VALUES(
#{id}

	,#{name}


	,#{password}


	,#{createTime}


	,#{updateTime}


	,#{description}


	,#{realName}


	,#{phone}


	,#{status}

)

mapper.update:
UPDATE Admin
SET 
id = #{id}

	,name = #{name}


	,password = #{password}


	,createTime = #{createTime}


	,updateTime = #{updateTime}


	,description = #{description}


	,realName = #{realName}


	,phone = #{phone}


	,status = #{status}

WHERE 
id=#{id}
