mapper.select:
	SELECT * FROM ProductOrder WHERE 1=1
	
	  	AND status = #{status}
	
	
	  	AND accountId = #{accountId}
	
	
	and createTime>=#{datemin}
    
    
	and #{datemax}>=createTime
    
	ORDER BY updateTime DESC ,createTime DESC
    limit #{start},#{length}

SELECT * FROM ProductOrder WHERE 1=1

	AND status = #{status}


	AND accountId = #{accountId}


	and createTime>=#{datemin}


	and #{datemax}>=createTime

ORDER BY updateTime DESC ,createTime DESC

	SELECT count(*) FROM ProductOrder WHERE 1=1
	
	  	AND status = #{status}
	
	
	  	AND accountId = #{accountId}
	
	
	and createTime>=#{datemin}
    
    
	and #{datemax}>=createTime
